sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ((isLoading && !g_form.isNewRecord()) || newValue === '' || oldValue == newValue) {
      return;
   }

  var getEmailAddr = new GlideAjax('GetEmailAddress');
  // Specify the getEmail method
  getEmailAddr.addParam('sysparm_name','getEmailAddress');
  // Pass the Requested for sys_id
  getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
  // Send the request to the server
  getEmailAddr.getXML(fillEmailField);

	function fillEmailField(request){

		var scriptIncludeAnswer = request.responseXML.documentElement.getAttribute("answer");
		g_form.setValue("u_requested_for_email", scriptIncludeAnswer);
	}



}
